-- Maak games database aan (wordt alleen uitgevoerd bij eerste init)
CREATE DATABASE IF NOT EXISTS games CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;

-- Geef rechten aan curiodevbox gebruiker
GRANT ALL PRIVILEGES ON games.* TO 'curiodevbox'@'%';
FLUSH PRIVILEGES;

USE games;

-- Maak games tabel
CREATE TABLE IF NOT EXISTS games (
    id INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    developer VARCHAR(255) NOT NULL,
    publisher VARCHAR(255),
    release_year INT,
    genre VARCHAR(100),
    platform VARCHAR(100),
    price DECIMAL(10, 2),
    description TEXT,
    rating DECIMAL(3, 1),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

